export const patientSate = {
    patients: []
}

export const patientReducer = (state, action) => {

    switch(action.type){
        case 'ADD_PATIENT':
            const newPatient = {
                id: action.id,
                name: action.name
            }
            const allPatients = [...state.patients, newPatient];
            return {patients: allPatients};
            break;
        case 'REMOVE_PATIENT':
            console.log(action);
            const remaining = state.patients.filter()
            return state;
        default:
            return state;
    }
}
